 if (alpha < alphaThreshold) discard;
 #endif
}
in vec4 color;
#if USE_TEXTURE
 in vec2 uv0;
 uniform sampler2D cc_spriteTexture;
#endif
vec4 frag () {
 vec4 o = vec4(1, 1, 1, 1);
 #if USE_TEXTURE
 o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
 #if IS_GRAY
 float gray = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
 o.r = o.g = o.b = gray;
 #endif
 #endif
 o *= color;
 ALPHA_TEST(o);
 return o;
}
layout(location = 0) out vec4 cc_FragColor;
