 vec4 transform = vec4(1.0, 0.0, 0.0, 1.0);
 if (orientation == 0) {
 transform = vec4(1.0, 0.0, 0.0, 1.0);
 } else if (orientation == 1) {
 transform = vec4(0.0, 1.0, -1.0, 0.0);
 } else if (orientation == 2) {
 transform = vec4(-1.0, 0.0, 0.0, -1.0);
 } else if (orientation == 3) {
 transform = vec4(0.0, -1.0, 1.0, 0.0);
